INSERT INTO home_page_sections VALUES(7,'why_choose','content','Why Choose Duha',NULL,'Our comprehensive curriculum combines Cambridge education with Islamic values, ensuring students receive a well-rounded education that prepares them for the challenges of the 21st century while maintaining strong Islamic principles. We are committed to fostering global citizens who are grounded in faith and equipped with modern knowledge.','Our comprehensive curriculum combines Cambridge education with Islamic values, ensuring students receive a well-rounded education that prepares them for the challenges of the 21st century while maintaining strong Islamic principles.',NULL,NULL,'{"image_url":"https:\/\/images.unsplash.com\/photo-1503676260728-1c00da094a0b?w=600&h=400&fit=crop"}',14,1,'2025-11-15 21:53:47','2025-11-15 21:53:47');
INSERT INTO home_page_sections VALUES(8,'children_responsibility','content','Your Children, Our Responsibility',NULL,'<p>Welcome to Duha International School, established in 2012 in Chittagong, Bangladesh. We are dedicated to providing holistic education that combines academic excellence with moral development.</p><p>Our commitment extends beyond the classroom, focusing on Islamic values and Cambridge curriculum integration to nurture well-rounded individuals who excel both academically and spiritually.</p>','Welcome to Duha International School, established in 2012 in Chittagong, Bangladesh. We are dedicated to providing holistic education that combines academic excellence with moral development.',NULL,NULL,'{"image_url":"https:\/\/images.unsplash.com\/photo-1481627834876-b7833e8f5570?w=600&h=400&fit=crop"}',15,1,'2025-11-15 21:53:47','2025-11-15 21:53:47');
