PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "pages" ("id" integer primary key autoincrement not null, "title" varchar not null, "slug" varchar not null, "content" text not null, "meta_title" varchar, "meta_description" text, "seo_keywords" text, "is_published" tinyint(1) not null default '0', "published_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "events" ("id" integer primary key autoincrement not null, "title" varchar not null, "excerpt" text not null, "description" text not null, "event_date" datetime not null, "location" varchar, "category" varchar, "is_featured" tinyint(1) not null default '0', "is_published" tinyint(1) not null default '0', "published_at" datetime, "created_at" datetime, "updated_at" datetime, "slug" varchar);
CREATE TABLE IF NOT EXISTS "notices" ("id" integer primary key autoincrement not null, "title" varchar not null, "excerpt" text not null, "content" text not null, "category" varchar, "is_important" tinyint(1) not null default '0', "is_published" tinyint(1) not null default '0', "published_at" datetime, "created_at" datetime, "updated_at" datetime, "slug" varchar);
CREATE TABLE IF NOT EXISTS "staff" ("id" integer primary key autoincrement not null, "name" varchar not null, "position" varchar not null, "bio" text, "email" varchar, "phone" varchar, "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime, "social_links" text);
CREATE TABLE IF NOT EXISTS "admission_applications" ("id" integer primary key autoincrement not null, "parent_name" varchar not null, "parent_email" varchar not null, "parent_phone" varchar not null, "parent_address" text not null, "student_name" varchar not null, "student_dob" date not null, "student_gender" varchar not null, "current_grade" varchar not null, "applying_grade" varchar not null, "previous_school" text, "medical_conditions" text, "additional_notes" text, "status" varchar not null default 'pending', "reviewed_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "career_applications" ("id" integer primary key autoincrement not null, "full_name" varchar not null, "email" varchar not null, "phone" varchar not null, "position_applied" varchar not null, "cover_letter" text not null, "resume_path" varchar not null, "experience" text, "education" text, "skills" text, "status" varchar not null default 'pending', "reviewed_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "media" ("id" integer primary key autoincrement not null, "model_type" varchar not null, "model_id" integer not null, "uuid" varchar, "collection" varchar not null default 'default', "name" varchar not null, "file_name" varchar not null, "mime_type" varchar, "path" varchar, "disk" varchar not null default 'public', "file_hash" varchar, "collection_name" varchar not null, "manipulations" varchar not null, "custom_properties" varchar not null, "generated_conversions" varchar not null, "responsive_images" varchar not null, "order_column" integer, "created_at" datetime, "updated_at" datetime, "conversions_disk" varchar, "size" integer);
CREATE TABLE IF NOT EXISTS "permissions" ("id" integer primary key autoincrement not null, "name" varchar not null, "guard_name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "roles" ("id" integer primary key autoincrement not null, "name" varchar not null, "guard_name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "model_has_permissions" ("permission_id" integer not null, "model_type" varchar not null, "model_id" integer not null, foreign key("permission_id") references "permissions"("id") on delete cascade, primary key ("permission_id", "model_id", "model_type"));
CREATE TABLE IF NOT EXISTS "model_has_roles" ("role_id" integer not null, "model_type" varchar not null, "model_id" integer not null, foreign key("role_id") references "roles"("id") on delete cascade, primary key ("role_id", "model_id", "model_type"));
CREATE TABLE IF NOT EXISTS "role_has_permissions" ("permission_id" integer not null, "role_id" integer not null, foreign key("permission_id") references "permissions"("id") on delete cascade, foreign key("role_id") references "roles"("id") on delete cascade, primary key ("permission_id", "role_id"));
CREATE TABLE IF NOT EXISTS "site_settings" ("id" integer primary key autoincrement not null, "site_name" varchar not null default 'Duha International School', "site_description" text, "contact_email" varchar, "contact_phone" varchar, "address" text, "created_at" datetime, "updated_at" datetime, "website_name" varchar, "website_tagline" varchar, "website_description" text, "logo_path" varchar, "favicon_path" varchar, "primary_email" varchar, "secondary_email" varchar, "primary_phone" varchar, "secondary_phone" varchar, "physical_address" text, "business_hours" text, "social_media_links" text, "default_currency" varchar not null default 'USD', "default_language" varchar not null default 'en', "supported_languages" text, "timezone" varchar not null default 'UTC', "meta_title" varchar, "meta_description" text, "meta_keywords" text, "og_title" varchar, "og_description" text, "og_image" varchar, "canonical_url" text, "maintenance_mode" tinyint(1) not null default '0', "maintenance_message" text, "maintenance_scheduled_at" datetime, "maintenance_scheduled_until" datetime, "footer_text" text, "copyright_notice" varchar, "primary_color" varchar not null default '#0F4C81', "secondary_color" varchar not null default '#1E3A8A', "accent_color" varchar not null default '#F4C430', "google_analytics_id" varchar, "custom_css" text, "custom_js" text, "email_notification_preferences" text, "advisors" text);
CREATE TABLE IF NOT EXISTS "home_page_sections" ("id" integer primary key autoincrement not null, "section_key" varchar not null, "section_type" varchar not null, "title" varchar, "subtitle" varchar, "content" text, "description" text, "button_text" varchar, "button_link" varchar, "data" text, "sort_order" integer not null default '0', "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "home_page_contents" ("id" integer primary key autoincrement not null, "section_key" varchar not null, "section_type" varchar not null, "title" varchar, "subtitle" varchar, "content" text, "description" text, "button_text" varchar, "button_link" varchar, "data" text, "sort_order" integer not null default '0', "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "subscribers" ("id" integer primary key autoincrement not null, "name" varchar, "email" varchar not null, "is_active" tinyint(1) not null default '1', "unsubscribed_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "announcements" ("id" integer primary key autoincrement not null, "message" text not null, "link" varchar, "link_text" varchar, "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "starts_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE UNIQUE INDEX "pages_slug_unique" on "pages" ("slug");
CREATE INDEX "media_model_type_model_id_index" on "media" ("model_type", "model_id");
CREATE UNIQUE INDEX "media_uuid_unique" on "media" ("uuid");
CREATE INDEX "media_order_column_index" on "media" ("order_column");
CREATE UNIQUE INDEX "permissions_name_guard_name_unique" on "permissions" ("name", "guard_name");
CREATE UNIQUE INDEX "roles_name_guard_name_unique" on "roles" ("name", "guard_name");
CREATE INDEX "model_has_permissions_model_id_model_type_index" on "model_has_permissions" ("model_id", "model_type");
CREATE INDEX "model_has_roles_model_id_model_type_index" on "model_has_roles" ("model_id", "model_type");
CREATE INDEX "home_page_sections_section_type_is_active_index" on "home_page_sections" ("section_type", "is_active");
CREATE INDEX "home_page_sections_sort_order_index" on "home_page_sections" ("sort_order");
CREATE UNIQUE INDEX "home_page_sections_section_key_unique" on "home_page_sections" ("section_key");
CREATE INDEX "home_page_contents_section_type_is_active_index" on "home_page_contents" ("section_type", "is_active");
CREATE INDEX "home_page_contents_sort_order_index" on "home_page_contents" ("sort_order");
CREATE UNIQUE INDEX "home_page_contents_section_key_unique" on "home_page_contents" ("section_key");
CREATE UNIQUE INDEX "subscribers_email_unique" on "subscribers" ("email");
CREATE UNIQUE INDEX "events_slug_unique" on "events" ("slug");
CREATE UNIQUE INDEX "notices_slug_unique" on "notices" ("slug");
CREATE INDEX "announcements_is_active_expires_at_starts_at_index" on "announcements" ("is_active", "expires_at", "starts_at");
CREATE INDEX "announcements_sort_order_index" on "announcements" ("sort_order");
CREATE INDEX "site_settings_maintenance_mode_index" on "site_settings" ("maintenance_mode");
COMMIT;
